import { FormControl, InputLabel, Select, MenuItem, Divider } from '@mui/material';
import { DeleteOutlined } from '@ant-design/icons';
import { Image, Row, Col } from 'antd';
import { useEffect, useState } from 'react';
import Form from './forms';
const EditStyle = ({
    tabImage,
    label,
    typeList,
    consData,
    detailImage,
    changeDetail
}: {
    tabImage: any;
    label: any;
    typeList: any;
    consData: any;
    detailImage: (label: string, index: number) => void;
    changeDetail: (data: any) => void;
}) => {
    const [value, setValue] = useState('');
    useEffect(() => {
        if (tabImage) {
            console.log(tabImage);
            console.log(typeList);
        }
    }, [tabImage]);
    return (
        <div className="flex min-h-[250px]">
            <div className="flex-1">
                <FormControl sx={{ flex: 1 }} color="secondary" fullWidth>
                    <InputLabel id="type">风格</InputLabel>
                    <Select
                        value={value}
                        onChange={(e: any) => {
                            changeDetail({ value: e.target.value, field: 'imageTemplate' });
                            setValue(e.target.value);
                        }}
                        labelId="type"
                        label="风格"
                    >
                        {typeList?.map((item: any) => (
                            <MenuItem value={item.id}>{item.name}</MenuItem>
                        ))}
                    </Select>
                </FormControl>
                {value && (
                    <div className="mt-[20px]">
                        <Row className="items-center" gutter={20}>
                            {typeList
                                .filter((val: any) => val.id === value)[0]
                                ?.variables?.map((el: any, index: number) =>
                                    el?.style === 'IMAGE' ? (
                                        <Col className="mt-[10px]" key={index}>
                                            <div className=" rounded-[8px] overflow-hidden relative">
                                                <Image
                                                    className="rounded"
                                                    width={100}
                                                    height={100}
                                                    preview={false}
                                                    src={el?.response?.data?.url}
                                                    fallback="data:image/png;base64,"
                                                />
                                                <div className="absolute w-full h-full top-0  opacity-50 z-1 flex justify-center text-[transparent] items-center hover:bg-[#000] hover:text-[#fff]">
                                                    <DeleteOutlined
                                                        onClick={() => {
                                                            detailImage(label, index);
                                                        }}
                                                        className="cursor-pointer hover:text-[red]"
                                                        rev={undefined}
                                                    />
                                                </div>
                                            </div>
                                        </Col>
                                    ) : (
                                        <Col key={index} sm={12} xs={24} md={6}>
                                            <Form
                                                changeValue={(data: any) => {
                                                    changeDetail({ ...data, field: el.field });
                                                }}
                                                values={consData[el.field]}
                                                item={el}
                                            />
                                        </Col>
                                    )
                                )}
                        </Row>
                    </div>
                )}
                <div className="mt-[20px] flex flex-wrap gap-2">
                    {tabImage?.map((item: any, index: number) => (
                        <div key={item.uid} className="rounded-[8px] overflow-hidden relative">
                            <Image className="rounded" width={100} height={100} preview={false} src={item?.response?.data?.url} />
                            <div className="absolute w-full h-full top-0  opacity-50 z-1 flex justify-center text-[transparent] items-center hover:bg-[#000] hover:text-[#fff]">
                                <DeleteOutlined
                                    onClick={() => {
                                        detailImage(label, index);
                                    }}
                                    className="cursor-pointer hover:text-[red]"
                                    rev={undefined}
                                />
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};
export default EditStyle;
