import { Card, Modal, IconButton, CardContent, Divider, ButtonGroup, Button } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import MainCard from 'ui-component/cards/MainCard';
import { Upload, Progress, Image } from 'antd';
import type { UploadProps } from 'antd';
import { PlusOutlined, DeleteOutlined } from '@ant-design/icons';
import { useState } from 'react';
import { getAccessToken } from 'utils/auth';
import _ from 'lodash-es';
const EditBackgroundImage = () => {
    const [widthScale, setWidthScale] = useState<number | null>(100);
    const [heightScale, setHeightScale] = useState<number | null>(100);
    const [imageList, setImageList] = useState<any[]>([]);
    const [open, setOpen] = useState(false);
    //上传图片
    const { Dragger } = Upload;
    const props: UploadProps = {
        name: 'imageFile',
        data: {
            width: widthScale,
            height: heightScale
        },
        showUploadList: false,
        action: `${process.env.REACT_APP_BASE_URL}${process.env.REACT_APP_API_URL}/llm/image/upscale`,
        headers: {
            Authorization: 'Bearer ' + getAccessToken()
        },
        multiple: true,
        maxCount: 20,
        accept: '.png, .jpg, .webp',
        onChange(info) {
            if (info.file.status === 'uploading') {
                if (info.file.response) {
                    return info.file;
                } else {
                    return {};
                }
            } else if (info.file.status === 'done') {
                const newValue = _.cloneDeep(imageList);
                newValue.push(info.file);
                setImageList(newValue);
                setOpen(true);
            }
        },
        onDrop(e) {
            console.log('Dropped files', e.dataTransfer.files);
        }
    };
    return (
        <div className="flex flex-wrap gap-2">
            <Dragger className="w-[240px] h-[240px]" {...props}>
                <div>
                    <p className="ant-upload-drag-icon">
                        <PlusOutlined rev={undefined} />
                    </p>
                    <p className="ant-upload-text">点击上传或直接将图片拖入区域</p>
                    <p className="ant-upload-hint">仅支持 .jpg/.png/.webp 格式</p>
                </div>
            </Dragger>
            {imageList.map((item, index) => (
                <div key={index} className="w-[240px] h-[240px] rounded-lg overflow-hidden relative border border-solid border-[#d9d9d9]">
                    <Image
                        width="100%"
                        className="object-cover	"
                        src={'data:image/jpeg;base64,' + item.response?.data.binary}
                        fallback="data:image/png;base64,"
                    />
                    {item.percent !== 100 && (
                        <div className="absolute bg-[#e5e7eb] left-0 top-0 w-full h-full opacity-70 flex items-center justify-center">
                            <Progress type="circle" percent={item.percent} />
                        </div>
                    )}
                </div>
            ))}
            <Modal open={open} onClose={() => setOpen(false)} aria-labelledby="modal-title" aria-describedby="modal-description">
                <MainCard
                    style={{
                        position: 'absolute',
                        width: '800px',
                        top: '10%',
                        left: '50%',
                        transform: 'translate(-50%, 0)',
                        maxHeight: '80%',
                        overflow: 'auto'
                    }}
                    title="上传列表"
                    content={false}
                    secondary={
                        <IconButton onClick={() => setOpen(false)} size="large" aria-label="close modal">
                            <CloseIcon fontSize="small" />
                        </IconButton>
                    }
                >
                    <CardContent sx={{ p: 2, display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
                        <Dragger className="w-[160px] h-[160px]" {...props}>
                            <div className="border-[#673ab7] text-[#673ab7] rounded-lg flex flex-col justify-center items-center cursor-pointer">
                                <PlusOutlined className="text-[20px] mb-[8px]" rev={undefined} />
                                <p>继续上传</p>
                            </div>
                        </Dragger>
                        {imageList.map((item, index) => (
                            <div key={index} className="w-[160px] h-[160px] rounded-lg relative overflow-hidden">
                                <Image
                                    width="100%"
                                    className="object-cover"
                                    preview={{
                                        visible: false,
                                        mask: (
                                            <div
                                                className="w-full h-full flex justify-center items-center"
                                                onClick={() => {
                                                    const newValue = _.cloneDeep(imageList);
                                                    newValue.splice(index, 1);
                                                    setImageList(newValue);
                                                }}
                                            >
                                                <DeleteOutlined className="text-[20px]" rev={undefined} />
                                            </div>
                                        )
                                    }}
                                    src={'data:image/jpeg;base64,' + item.response?.data?.binary}
                                    fallback="data:image/png;base64,"
                                />
                                {item.percent !== 100 && (
                                    <div className="absolute bg-[#e5e7eb] left-0 top-0 w-full h-full opacity-70 flex items-center justify-center">
                                        <Progress type="circle" percent={item.percent} />
                                    </div>
                                )}
                            </div>
                        ))}
                    </CardContent>
                    <ButtonGroup className="mb-[16px] flex justify-center" size="small" color="secondary">
                        <Button key="one">x2</Button>
                        <Button key="two">x4</Button>
                        <Button key="three">x8</Button>
                        <Button key="frou">x16</Button>
                    </ButtonGroup>
                    <Divider />
                    <div className="flex justify-between px-[16px] items-center py-[16px]">
                        <div>
                            <div className="font-bold text-sm leading-5">已上传{imageList.length}/20张</div>
                            <div className="text-sm leading-4">支持多张图片同时上传，仅支持 JPG/PNG/WEBP 格式图片</div>
                        </div>
                        <div>
                            <Button size="small" variant="outlined">
                                取消
                            </Button>
                            <Button className="ml-[8px]" size="small" color="secondary" variant="outlined">
                                抠图
                            </Button>
                        </div>
                    </div>
                </MainCard>
            </Modal>
        </div>
    );
};
export default EditBackgroundImage;
